document.addEventListener('DOMContentLoaded', () => {
			const issuesMenu = document.querySelector('#ifq-issues-menu-692c7cc7b1027');
			const issuesList = issuesMenu.querySelector('[data-el="ifq-issues-list"]');
			const issuesListWidth = issuesList?.scrollWidth || 0;
			const issuesListVisibleWidth = issuesList?.offsetWidth || 0;
			const issuesButtonPrev = issuesMenu.querySelector('[data-el="ifq-issues-button-prev"]');
			const issuesButtonNext = issuesMenu.querySelector('[data-el="ifq-issues-button-next"]');
			const issuesNavigation = issuesMenu.querySelector('.ifq-issues-menu__navigation');

if (issuesListWidth <= issuesListVisibleWidth) {
				issuesNavigation.style.display = 'none';
			} else {
				issuesNavigation.style.display = 'flex';

issuesButtonNext.addEventListener('click', () => {
					issuesList.scrollBy({
						left: issuesListVisibleWidth,
						behavior: 'smooth'
					});
					issuesButtonPrev.disabled = false;

if (issuesList.scrollLeft + issuesListVisibleWidth >= issuesListWidth) {
						issuesButtonNext.disabled = true;
					}
				});

issuesButtonPrev.addEventListener('click', () => {
					issuesList.scrollBy({
						left: -issuesListVisibleWidth,
						behavior: 'smooth'
					});
					issuesButtonNext.disabled = false;

if (issuesList.scrollLeft <= 0) {
						issuesButtonPrev.disabled = true;
					}
				});
			}
		});

“La Santa Sede già da diversi anni pubblicamente appoggia la soluzione dei due Stati. Sappiamo tutti che in questo momento Israele ancora non accetta questa soluzione, ma la vediamo come unica soluzione al conflitto che continuamente vivono” israeliani e palestinesi. Lo ha detto Papa Leone XIV parlando con i giornalisti a bordo del volo che lo ha portato da Istanbul a Beirut nell’ambito del suo viaggio in Turchia e Libano. “Noi siamo anche amici di Israele e cerchiamo con le due parti di essere una voce mediatrice che possa aiutare ad avvicinarci a una soluzione giusta per tutti”, ha aggiunto. Prevost afferma di aver “parlato di questo” con il presidente turco Recep Tayyip Erdoğan, che, dice, “è certamente d’accordo” sull’ipotesi dei due Stati: “La Turchia potrebbe giocare un ruolo importante in questo”, ha sottolineato.

