function calculateVideoPercent(duration, position) {
                                    if (duration <= 0) {
                                        return 0;
                                    }

var percent = (position / duration) * 100;

var thresholds = [10, 25, 50, 75, 90];
                                    for (var i = 0; i < thresholds.length; i++) {
                                        if (percent < thresholds[i]) {
                                            return thresholds[i - 1] || 0;
                                        }
                                    }
                                    return 90;
                                }

