					const iconShow = otherToggle.getAttribute("data-icon-show");
					const iconHide = otherToggle.getAttribute("data-icon-hide");

if (textSpan.textContent !== textHide) {
						textSpan.textContent = textShow;
						img.src = iconShow;
						img.alt = textShow;
