                                        } else if (video_length <= 6000) {
                                            intervalDuration = 100000; // 100 secondi
                                        } else {
                                            intervalDuration = 30000; // Default a 60 secondi per lo streaming/live senza durata predefinita
                                        }

return intervalDuration;
