                                            console.log('[JWP] => content buffering', contentPlayhead);
                                            nSdkInstance.ggPM('stop', contentPlayhead);
                                        }
                                    sendTagManagerVideoPlayer(category_video, 'buffering', 'ifq', videoTitle, jwp.getPlaylistItem().file, Math.round(jwp.getDuration()), currentPlayTime, calculateVideoPercent(Math.round(jwp.getDuration()),currentPlayTime), author, publicationDate, 'Jw Player');

})

