                                        if (hasPreroll && !prerollHasEnded) {
                                            prerollHasEnded = true;
                                            console.log('[JWP] => manual preroll end', prerollPlayhead);
                                            nSdkInstance.ggPM('stop', prerollPlayhead);
                                        }
                                        console.log('[JWP] => content start');
                                        nSdkInstance.ggPM('loadMetadata', contentMetadataObject);
                                        if (typeof(onPlay) === "function") onPlay();

jwp_startInterval();
                                    sendTagManagerVideoPlayer(category_video, 'start', 'ifq', videoTitle, jwp.getPlaylistItem().file, Math.round(jwp.getDuration()), currentPlayTime, calculateVideoPercent(Math.round(jwp.getDuration()),currentPlayTime), author, publicationDate, 'Jw Player');    
                                    });

jwp.on('seek', function(e) {
                                        if (!isSeeking) {
                                            isSeeking = true;
