document.addEventListener("DOMContentLoaded", function() {

// Funzione per gestire il cambio dei prezzi
			function updatePrice(offer, isYearly) {
				const priceOld = offer.querySelector('.ifq-paywall__price-old');
				const priceNext = offer.querySelector('.ifq-paywall__price-next');
				const priceMain = offer.querySelector('.ifq-paywall__price-main');
				const priceDecimal = offer.querySelector('.ifq-paywall__price-decimal');
				const priceInfo = offer.querySelector('.ifq-paywall__price-info');
				const priceSaving = offer.querySelector('.ifq-paywall__price-saving');
				const offerUrl = offer.querySelector('.ifq-paywall__actions-button');

if (priceOld) {
					priceOld.textContent = isYearly ? priceOld.dataset.oldPriceYearly : priceOld.dataset.oldPriceMonthly;
				}
				if (priceMain) {
					priceMain.textContent = isYearly ? priceMain.dataset.newPriceMainYearly : priceMain.dataset.newPriceMainMonthly;
				}
				if (priceDecimal) {
					priceDecimal.textContent = isYearly ? priceDecimal.dataset.newPriceDecimalYearly : priceDecimal.dataset.newPriceDecimalMonthly;
				}
				if (priceInfo) {
					priceInfo.textContent = isYearly ? priceInfo.dataset.newPriceInfoYearly : priceInfo.dataset.newPriceInfoMonthly;
				}
				if (priceSaving) {
					priceSaving.textContent = isYearly ? priceSaving.dataset.priceSavingYearly : priceSaving.dataset.priceSavingMonthly;
				}
				if (priceNext) {
					priceNext.textContent = isYearly ? priceNext.dataset.nextPriceYearly : priceNext.dataset.nextPriceMonthly;
				}
				if (offerUrl) {
					offerUrl.href = isYearly ? offerUrl.dataset.yearlyUrl : offerUrl.dataset.monthlyUrl;
				}
			}

// Gestisci il toggle per le opzioni di abbonamento (annuale/mensile) [indipendente]
			/*document.querySelectorAll('.ifq-paywall__toggle-switch input').forEach(toggle => {
				toggle.addEventListener('change', function() {
					const isYearly = this.checked;
					const offer = this.closest('.ifq-paywall__offer');
					if (offer) updatePrice(offer, isYearly);
				});
			});*/

// Gestisci il toggle per le opzioni di abbonamento (annuale/mensile) [sincronizzato]
			document.querySelectorAll('.ifq-paywall__toggle-switch input').forEach(toggle => {
				toggle.addEventListener('change', function() {
					const isYearly = this.checked;
					const toggleName = this.name;

// Sincronizza tutti i toggle con lo stesso name
					document.querySelectorAll(`.ifq-paywall__toggle-switch input[name="${toggleName}"]`).forEach(otherToggle => {
						if (otherToggle !== this) {
							otherToggle.checked = isYearly;
						}
						// Applica anche l'aggiornamento prezzi
						const offer = otherToggle.closest('.ifq-paywall__offer');
						if (offer) updatePrice(offer, isYearly);
					});
				});
			});

// Funzione per gestire l'apertura/chiusura dei contenuti inclusi
			function toggleIncludedContent(toggleButton) {
				const currentPaywall = toggleButton.closest('.ifq-paywall');
				const offer = toggleButton.closest('.ifq-paywall__offer');
				const subscription = toggleButton.getAttribute('data-subscription');
				const content = offer.querySelector('.ifq-paywall__included-content[data-subscription="' + subscription + '"]');
				const allContents = currentPaywall.querySelectorAll('.ifq-paywall__included-content');
				const allToggles = currentPaywall.querySelectorAll('.ifq-paywall__included-toggle');

// Nascondi altri contenuti e reset dei toggle
				allContents.forEach(otherContent => {
					if (otherContent.dataset.subscription != subscription) {
						otherContent.classList.add('ifq-paywall__included-content--hidden')
					} else {
						otherContent.classList.toggle('ifq-paywall__included-content--hidden');
					}
				});
				allToggles.forEach(otherToggle => {
					const textSpan = otherToggle.querySelector("span");
					const img = otherToggle.querySelector("img");
					const textShow = otherToggle.getAttribute("data-text-show");
					const textHide = otherToggle.getAttribute("data-text-hide");
					const iconShow = otherToggle.getAttribute("data-icon-show");
					const iconHide = otherToggle.getAttribute("data-icon-hide");

if (textSpan.textContent !== textHide) {
						textSpan.textContent = textShow;
						img.src = iconShow;
						img.alt = textShow;
					}
					if (otherToggle.dataset.subscription == subscription) {
						if (textSpan.textContent === textShow) {
							textSpan.textContent = textHide;
							img.src = iconHide;
							img.alt = textHide;
						} else {
							textSpan.textContent = textShow;
							img.src = iconShow;
							img.alt = textShow;
						}
					}

});
			}

// Aggiungi l'evento click per tutti i toggle di visibilit√† dei contenuti inclusi
			document.querySelectorAll('.ifq-paywall__included-toggle').forEach(toggleButton => {
				toggleButton.addEventListener("click", function() {
					toggleIncludedContent(toggleButton);
				});
			});
					});

