					const isYearly = this.checked;
					const toggleName = this.name;

// Sincronizza tutti i toggle con lo stesso name
					document.querySelectorAll(`.ifq-paywall__toggle-switch input[name="${toggleName}"]`).forEach(otherToggle => {
						if (otherToggle !== this) {
