function _setTopSpacing(){
var distanceFromTop  = 20;
if( $window.width() > 1024 ) {
topSpacing = distanceFromTop + $('.nav-wrap').outerHeight();
} else {
topSpacing = distanceFromTop;
}
return topSpacing;
}
