					priceNext.textContent = isYearly ? priceNext.dataset.nextPriceYearly : priceNext.dataset.nextPriceMonthly;
				}
				if (offerUrl) {
					offerUrl.href = isYearly ? offerUrl.dataset.yearlyUrl : offerUrl.dataset.monthlyUrl;
				}
			}

