						if (textSpan.textContent === textShow) {
							textSpan.textContent = textHide;
							img.src = iconHide;
							img.alt = textHide;
						} else {
							textSpan.textContent = textShow;
							img.src = iconShow;
							img.alt = textShow;
						}
					}

});
			}

// Aggiungi l'evento click per tutti i toggle di visibilità dei contenuti inclusi
