                        'interaction_type': 'conversion',
                        'conversion_value': 1
                    });
                    break;

                default:
                    console.log('Azione DriveK non riconosciuta:', action);
                    pushToDataLayer('drivek_unknown_action', {
                        'event_category': 'DriveK Widget',
                        'event_label': action,
                        'interaction_type': 'unknown'
                    });
