				document.querySelectorAll('.ifq-paywall__toggle-switch input').forEach(t => {
					if (t !== firstToggle) {
						t.checked = true;
						const offer = t.closest('.ifq-paywall__offer');
						if (offer) updatePrice(offer, true);
					}
				});
